$where:
graph npa:graph {
  ?np rdfs:label ?label ;
      npa:hasValidSignatureForPublicKey ?pubkey ;
      dct:created ?date .
  optional { ?np npx:hasNanopubType ?type . }
}
?np search:matches [
  search:query "fair";
  search:property rdfs:label;
  search:score ?score;
   search:snippet ?snippet ] .
